mapper.update:
UPDATE user
SET refresh_token = #{refreshCode}
WHERE id = #{id}

mapper.select:
SELECT refresh_token
FROM user
WHERE id = #{id}

mapper.insert:
INSERT INTO user
(
    idx
    , id
    , password
    , name
    , create_time
    , user_type
    , grade
    , class_name
    , class_no
)
VALUES
(
    #{idx}
    , #{id}
    , #{password}
    , #{name}
    , now()
    , #{userType}
    , #{grade}
    , #{className}
    , #{classNo}
)
