mapper.select:

FROM card AS a
WHERE a.user_id =
    (
        SELECT idx FROM `user`
        WHERE school_number = #{schoolNumber}
    )

mapper.insert:
INSERT INTO card
(
    idx
    , user_id
    , nfc_id
    , create_time
    , last_time
)
VALUES
(
    #{idx}
    , #{userId}
    , #{nfcId}
    , now()
    , now()
)
